//全选
function all_Click(backname,checkname,allname){
 	var chk=document.getElementsByName(checkname);
 	
	if(document.getElementsByName(allname)[0].checked){
		 for(var i=0;i<chk.length;i++){ 
		     document.getElementsByName(checkname)[i].checked=true;   
		 } 
	}else{
		 for(var i=0;i<chk.length;i++){ 
		     document.getElementsByName(checkname)[i].checked=false;   
		 } 
	}
	  document.getElementsByName(backname)[0].checked=false; 
}
 

//反选   
function not_Click(allname,checkname){   
  document.getElementsByName(allname)[0].checked=false;   
  var chk=document.getElementsByName(checkname); 
  for(var i=0;i<chk.length;i++){ 
      if(document.getElementsByName(checkname)[i].checked)   
    	 document.getElementsByName(checkname)[i].checked=false;   
     else  
    	 document.getElementsByName(checkname)[i].checked=true;   
 }   
}  

function hasCheckBoxName(checkBoxName){
	var rtn = 0
	var checkBoxName=document.getElementsByName(checkBoxName);
	for(var i=0;i<checkBoxName.length;i++){
		if(checkBoxName[i].checked==true ){
			rtn = rtn +1;
		}
	}
	if (rtn>0)
		return true;
	else
		return false;

}

 

function trChecked(ele,ff){
 	 
	if ( ele!=null) {
 	     $(ele).parent().find('tr:gt(0)').find('td').removeAttr("style")

  	     $(ele).find('td').css("background","#b7c0c7")
	     if(document.getElementById('idvalue')!=null)
	     document.getElementById('idvalue').parentNode.removeChild(document.getElementById('idvalue'))
	     var inp=document.createElement("input");
	     inp.type="hidden";
	     inp.name="idvalue";
	     inp.id="idvalue";
	     inp.value=ele.getAttribute("idvalue");
	     document.getElementsByName(ff)[0].appendChild(inp);  
	 	}
  }

