//全选
function all_Click(backname,checkname,allname){
 	var chk=document.getElementsByName(checkname);
 	
	if(document.getElementsByName(allname)[0].checked){
		 for(var i=0;i<chk.length;i++){ 
		     document.getElementsByName(checkname)[i].checked=true;   
		 } 
	}else{
		 for(var i=0;i<chk.length;i++){ 
		     document.getElementsByName(checkname)[i].checked=false;   
		 } 
	}
	  document.getElementsByName(backname)[0].checked=false; 
}
 

//反选   
function not_Click(allname,checkname){   
  document.getElementsByName(allname)[0].checked=false;   
  var chk=document.getElementsByName(checkname); 
  for(var i=0;i<chk.length;i++){ 
      if(document.getElementsByName(checkname)[i].checked)   
    	 document.getElementsByName(checkname)[i].checked=false;   
     else  
    	 document.getElementsByName(checkname)[i].checked=true;   
 }   
}  

