	var checkBoxName=document.getElementsByName(checkBoxName);
	for(var i=0;i<checkBoxName.length;i++){
		if(checkBoxName[i].checked==true ){
			rtn = rtn +1;
		}
	}
	if (rtn>0)
		return true;
	else
		return false;

}

 

