/**
	设置
	setleaf( '绑定的ID', [{
		leafId:'',  下级关联ID
		initVal:'', 初值
		tabName:'', 下级关联表名
		key:'',  select 的 option 的 Key
		value:'', select 的 option 的 Value
		condition:[{
			relationKey:'', 关联的字段名
			valId:'', 关联的值的DOM ID
			relation:'',  衔接方式 如: and, or 等
			operator:'', 关联符号  如: like, =, >, <, <>, !=, <=, >= 
			prefix:'', 后缀 例如: like 时想要使用左关联 就填写% 即形成 '%'||值
			suffix:'' 前缀 例如: like 时想要使用右关联 就填写% 即形成 值||'%'
		}], 
		sort:'', 排序字段
		filter:'' 过滤条件, 如:kyf != 1
		}]);
	级联的初始化
	initializeleaf('最高级ID')
*/
/**
 * 级联方法
 * @author KevinChu
 * @date 2012-07-21
 * @param obj select
 * @returns
 */
function queryLeaf(obj) {
	var json = $(obj).attr('leafOption');
	var list = eval("("+json+")");
	for(var i=0;i<list.length;i++){ 
		var map = list[i];
		$('#' + map.leafId).html('');
		$('#' + map.leafId).append('<option value="" selected="selected">----请选择----</option>')
		var thisVal = $(obj).val();
		if(thisVal == null || thisVal == '') {
			return;
		}
		var tabName = map.tabName
		var condition = '';
		var conditionList = eval("("+map.condition+")");
		for(var j=0;j<conditionList.length;j++){ 
			var conditionMap = conditionList[j];
			var val = $('#' + conditionMap.valId).val()
			if(conditionMap.prefix != 'undefined' && conditionMap.prefix != null && conditionMap.prefix != ''){
				val = conditionMap.prefix + val;
			}
			if(conditionMap.suffix != 'undefined' && conditionMap.suffix != null && conditionMap.suffix != ''){
				val += conditionMap.suffix;
			}
			if(conditionMap.relation != 'undefined' && conditionMap.relation != null && conditionMap.relation != ''){
				condition += ' ' + conditionMap.relation
			}
			condition += ' ' + conditionMap.relationKey + ' ' + conditionMap.operator + " '" + val + "'";
		}
		if(map.filter != '' && map.filter != null && map.filter != 'undefined') {
			condition += ' and ' + map.filter
		}
		if(map.sort != '' && map.sort != null && map.sort != 'undefined') {
			condition += ' order by ' + map.sort;
		}
		var param = {'tabName':tabName, 'condition':condition};
		var url=getRootPath()+"queryLeaf";
		$.ajax({   
		type:"POST",  
		url:url,  
		data:param ,
		success: function(result){
						for(var i=0;i<result.length;i++){
							$('#' + map.leafId).append('<option value="' + result[i][map.key] + '">' + result[i][map.value] + '</option>')
						}
					},
		dataType: "json",
		async:false 
		})
	}
}
/**
 * 初始化
 * @author KevinChu
 * @date 2012-07-21
 * @param id 最高级id
 */
function initLeaf( id) {
	var json = $('#' + id).attr('leafOption');
	if( json != null){
		queryLeaf( $('#' + id));
		var list = eval("("+json+")");
		for(var i=0;i<list.length;i++){ 
			var map = list[i];
			var leafId = map.leafId;
			var initVal = $('#' + leafId).attr('initVal');
			if( initVal == ""){
				$('#' + leafId).val('')
			}
			else{
				$('#' + leafId).val( initVal)
			}
			initLeaf( leafId);
		}
	}
}
/**
 * 设置级联
 * @author KevinChu
 * @date 2012-07-21
 * @param id ID
 * @param option 设置
 */
function setLeaf( id, option) {
	var optionmsg = '['
	$.each( option, function( i, obj){
		var condition = "[";
		$.each( obj.condition, function( j, condition_obj){
			if(condition != "[") condition += ',';
			condition += "{ \"relationKey\":\"" + condition_obj.relationKey + "\", \"valId\":\"" + condition_obj.valId + "\", \"relation\":\"" + condition_obj.relation + "\", \"operator\":\"" + condition_obj.operator + "\", \"prefix\":\"" + condition_obj.prefix + "\", \"suffix\":\"" + condition_obj.suffix + "\"}";
		});
		condition += ']';
		var leafId = obj.leafId;
		$('#' + leafId).attr('initVal', obj.initVal);
		if(optionmsg != "[") optionmsg += ',';
		optionmsg += "{'leafId':'" + obj.leafId+ "', 'tabName':'" + obj.tabName+ "', 'key':'" + obj.key+ "', 'value':'" + obj.value+ "', 'condition':'" + condition + "', 'sort':'" + obj.sort+ "', 'filter':'" + obj.filter+ "'}";
	});
	optionmsg += ']'
	$('#' + id).attr('leafOption', optionmsg);
	$('#' + id).live('change',function(){
		queryLeaf(this);    
	});
}
/**
 * 获取路径
 * @return
 */
function getRootPath(){
	var pathName = window.location.pathname.substring(1);
	var webName = pathName == '' ? '' : pathName.substring(0, pathName.indexOf('/'));
	return window.location.protocol + '//' + window.location.host + '/'+ webName + '/';
}

